mapper.select:
select 
from mall_verify_code_record
where type = #{type} and send_time >= #{time}

mapper.update:
update mall_verify_code_record
set
  count = #{count},
  code = #{code},
  send_time = #{sendTime},
  expire_time = #{expireTime}
where
  target = #{target}
  and
  type = #{verifyCodeType}
